const video = document.getElementById('webcam');
const liveView = document.getElementById('liveView');
const demosSection = document.getElementById('demos');
const enableWebcamButton = document.getElementById('webcamButton');


//----------------------------------------------------------------------
// We can now add some assistive functions to check if the browser we are using supports accessing the webcam stream via getUserMedia:
//----------------------------------------------------------------------


function getUserMedia(constraints) {
  // if Promise-based API is available, use it
  if (navigator.mediaDevices) {
    return navigator.mediaDevices.getUserMedia(constraints);
  }
    
  // otherwise try falling back to old, possibly prefixed API...
  var legacyApi = navigator.getUserMedia || navigator.webkitGetUserMedia ||
    navigator.mozGetUserMedia || navigator.msGetUserMedia;
    
  if (legacyApi) {
    // ...and promisify it
    return new Promise(function (resolve, reject) {
      legacyApi.bind(navigator)(constraints, resolve, reject);
    });
  }
}

 

// If webcam supported, add event listener to button for when user
// wants to activate it to call enableCam function which we will 
// define in the next step.
enableWebcamButton.addEventListener('click', enableCam);


//----------------------------------------------------------------------
// Next we can fill out the code for the previously empty enableCam function we defined above.
//----------------------------------------------------------------------


// Enable the live webcam view and start classification.
function enableCam(event) {
  // Only continue if the COCO-SSD has finished loading.
  if (!model) {
    return;
  }
  
  // Hide the button once clicked.
  event.target.classList.add('removed');  
  
  // getUsermedia parameters to force video but not audio.
  const constraints = {
    video: true
  };

  // Activate the webcam stream. (https://whatwebcando.today/camera-microphone.html)

  if (!navigator.mediaDevices && !navigator.getUserMedia && !navigator.webkitGetUserMedia &&
    !navigator.mozGetUserMedia && !navigator.msGetUserMedia) {
    alert('User Media API not supported.');
    return;
  }

  getUserMedia(constraints)
    .then(function (stream) {
      //var mediaControl document.querySelector(type);
      
      if ('srcObject' in video) {
        video.srcObject = stream;
      } else if (navigator.mozGetUserMedia) {
        video.mozSrcObject = stream;
      } else {
        video.src = (window.URL || window.webkitURL).createObjectURL(stream);
      }
      
      //video.play();
      video.addEventListener('loadeddata', predictWebcam);

    })
    .catch(function (err) {
      alert('Webcam is not supported by your browser. Error: ' + err);
    });


}



